        public static void instanciarBD()
        {
            sistemaContext db = new sistemaContext();
            if (db.Juegos.Count() == 0)
            {
                cargarJuegos();
            }
            if (db.Usuarios.Count() <=4)
            {                
                List<string> lista = db.Usuarios.Select(i => i.IdUsuario).ToList();
                List<int> juegos = db.Juegos.Select(i => i.Id).ToList();
                foreach (var user in lista)
                {
                    agregarALibreria(user, juegos);
                    agregarAWl(user, juegos);
                }
            }             
        }

        private static void agregarAWl(string idUsuario, List<int> juegos)
        {
            sistemaContext db = new sistemaContext();
            try
            {
                foreach (var idJuego in juegos)
                {
                    db.Wishlist.Add(new WishList() { IdUsuario = idUsuario, IdJuego = idJuego });
                }
                db.SaveChanges();
            }
            catch (Exception)
            {

            }
        }

        private static void agregarALibreria(string idUsuario, List<int> juegos)
        {
            sistemaContext db = new sistemaContext();
            try
            {
                foreach (var idJuego in juegos)
                {
                    db.Libreria.Add(new Libreria() { IdUsuario = idUsuario, IdJuego = idJuego });
                }
                db.SaveChanges();
            }
            catch (Exception)
            {

            }            
        }
